@techreport{NBERw4307, title = "Employer Provided Health Insurance and Retirement Behavior", author = "Alan L. Gustman and Thomas L. Steinmeier", institution = "National Bureau of Economic Research", type = "Working Paper", series = "Working Paper Series", number = "4307", year = "1993", month = "March", doi = {10.3386/w4307}, URL = "http://www.nber.org/papers/w4307", abstract = {This paper analyzes the effects on retirement of employer provided health benefits to workers and retirees. Retiree health benefits delay retirement until age of eligibility, and then accelerate it. With a base case of no retiree health coverage, granting retiree health coverage to all those with employer coverage while working accelerates retirement age by less than one month. Valuing benefits at costs of private health insurance to unaffiliated individuals, rather than at group rates, increases the effect. Ignoring retiree health benefits in retirement models creates only a small bias. Changing health insurance policies has a small effect on retirement.}, }
