@article {Gerber:1954-08-01T00:00:00:0003-7028:120, author = "Gerber, W. O. and Tobin, W. H.", title = "A Spectrochemical Method for the Analysis of M-252 Nickel Base High Temperature Alloy and the Preparation of Standards by Powder Metallurgy", journal = "Applied Spectroscopy", volume = "8", number = "3", year = "1954-08-01T00:00:00", abstract = "M-252 nickel base high temperature alloy is completely analyzed by instruments at the general Electric River Works. Molybdenum, cobalt, iron, and chromium are determined by X-ray fluorescence; titanium, aluminum, manganese, and silicon by ultraviolet emission spectroscopy and carbon by the high frequency induction furnace. The remainder, nickel, is calculated by difference.", pages = "120-125", url = "http://www.ingentaconnect.com/content/sas/sas/1954/00000008/00000003/art00004", doi = "doi:10.1366/000370254774634549" }
